import React, { Component } from 'react';
import axios from 'axios';
import fire from '../fire'
// const URL = 'https://techjobs100.herokuapp.com/'
const URL = 'http://localhost:4000/'

export default class EditJob extends Component {

    constructor(props) {
        super(props);
        console.log(props)

        this.onChange = this.onChange.bind(this);
        this.onSubmit = this.onSubmit.bind(this);

        this.state = {
            postedBy: fire.auth().currentUser.uid,
            dateCreated: new Date().getUTCMilliseconds(),
            isAccepting: false,
            company: '',
            profile: '',
            openFor: '',
            description: '',
            profileURL: '',
            applicants: []
        }
    }

    componentDidMount() {
        console.log('Job request sent')
        if(this.props.job) {
            axios.get(`${URL}jobs/` + this.props.job._id)
                .then(response => {
                    this.setState(Object.assign({}, response.data))
                })
                .catch(function (error) {
                    console.log(error);
                })
        }
    }

    onChange(e) {
        this.setState(Object.assign({}, this.state, e))
    }

    onSubmit(e) {
        e.preventDefault();
        const obj = this.state
        const id = this.props.job ? 'create' : this.props.job._id
        axios.post(`${URL}jobs/update/`+id, obj)
            .then(res => console.log(res.data));

        this.props.history.push('/');
    }

    render() {
        return (
            <div className="container col-5">
                <h3 align="center">{this.props.job ? 'Create post' : 'Edit post'}</h3>
                <h6 align="center">If any field is not available kindly leave it empty</h6>
                <br/><br/>
                <form onSubmit={this.onSubmit}>
                    <div className="form-group row">
                        <label className="col-sm-2 col-form-label">Company</label>
                        <div className="col-sm-10">
                            <input
                                type="text"
                                className="form-control"
                                value={this.state.company}
                                onChange={val => this.onChange({company: val.target.value})}
                            />
                        </div>
                    </div>
                    <div className="form-group row">
                        <label className="col-sm-2 col-form-label">Profile</label>
                        <div className="col-sm-10">
                            <input
                                type="text"
                                className="form-control"
                                value={this.state.profile}
                                onChange={val => this.onChange({profile: val.target.value})}
                            />
                        </div>
                    </div>
                    <div className="form-group row">
                        <label className="col-sm-2 col-form-label">Open for</label>
                        <div className="col-sm-10">
                            <input
                                type="text"
                                className="form-control"
                                value={this.state.openFor}
                                onChange={val => this.onChange({openFor: val.target.value})}
                            />
                        </div>
                    </div>
                    <div className="form-group row">
                        <label className="col-sm-2 col-form-label">Description</label>
                        <div className="col-sm-10">
                            <input
                                type="text"
                                className="form-control"
                                maxLength={100}
                                placeholder={"Max 100 chars"}
                                value={this.state.description}
                                onChange={val => this.onChange({description: val.target.value})}
                            />
                        </div>
                    </div>
                    <div className="form-group row">
                        <label className="col-sm-2 col-form-label">Profile URL</label>
                        <div className="col-sm-10">
                            <input
                                type="url"
                                className="form-control"
                                value={this.state.profileURL}
                                onChange={val => this.onChange({profileURL: val.target.value})}
                            />
                        </div>
                    </div>
                    <div className="form-group row custom-control custom-switch">
                        <label className="col-sm-2 col-form-label">Accepting</label>
                        <div className="col-sm-10">
                            <input
                                value={this.state.isAccepting}
                                onChange={val => this.onChange({isAccepting: val.target.value})}
                                type="checkbox" className="custom-control-input"/>
                        </div>
                    </div>
                    <br/>
                    <div className="form-group">
                        <input type="submit" value="Update Profile" className="btn btn-primary" />
                    </div>
                    <br/>
                    <br/>
                </form>
            </div>
        )
    }
}
